import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Translate } from '@google-cloud/translate/build/src/v2';

function Article() {
  const { url } = useParams();
  const [article, setArticle] = useState(null);
  const [translatedArticle, setTranslatedArticle] = useState(null);
  const [language, setLanguage] = useState('original');

  useEffect(() => {
    const fetchArticle = async () => {
      try {
        // Supondo que você esteja buscando o artigo completo usando a URL do artigo
        const response = await axios.get(url);
        setArticle(response.data);
      } catch (error) {
        console.error('Erro ao buscar artigo:', error);
      }
    };

    fetchArticle();
  }, [url]);

  const translateArticle = async () => {
    try {
      const translate = new Translate({ key: process.env.REACT_APP_GOOGLE_CLOUD_API_KEY });
      const [translation] = await translate.translate(article.content, 'pt');
      setTranslatedArticle(translation);
    } catch (error) {
      console.error('Erro ao traduzir o artigo:', error);
    }
  };

  useEffect(() => {
    if (language === 'portuguese' && article) {
      translateArticle();
    }
  }, [language, article]);

  return (
    <div className="article-page">
      <div className="language-toggle">
        <button onClick={() => setLanguage('original')}>Mostrar Original</button>
        <button onClick={() => setLanguage('portuguese')}>Mostrar em Português</button>
      </div>
      {language === 'original' ? (
        <div dangerouslySetInnerHTML={{ __html: article?.content }} />
      ) : (
        <div dangerouslySetInnerHTML={{ __html: translatedArticle }} />
      )}
    </div>
  );
}

export default Article;
