import React, { useState, useEffect } from 'react';
import api from '../api';
import NewsList from '../components/NewsList'; // Importando o componente NewsList

function Home() {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await api.get('/top-headlines', {
          params: {
            country: 'us',
            pageSize: 5, // Ajuste para a quantidade desejada de artigos
          },
        });
        setArticles(response.data.articles);
      } catch (error) {
        console.error('Erro ao buscar artigos:', error);
      }
    };

    fetchArticles();
  }, []);

  return (
    <div>
      <h1>Notícias</h1>
      <NewsList articles={articles} /> {/* Inserindo o NewsList e passando os artigos como props */}
    </div>
  );
}

export default Home;
